SELECT us.id,us.user_id,us.class_code,us.code_key,us.score_amount,us.status,us.create_time,sd.class_desc FROM user_score us, system_dict sd WHERE us.class_code = sd.class_code AND us.code_key = sd.code_key AND us.user_id = #{userId,jdbcType=VARCHAR}
AND us.id <= #{cursor,jdbcType=BIGINT}
ORDER BY id DESC LIMIT #{count,jdbcType=INTEGER}

    SELECT count(1) FROM user_score
	WHERE user_id = #{userId,jdbcType=VARCHAR}
	AND class_code = #{classCode,jdbcType=VARCHAR}
	AND date(create_time) = date(#{createTime,jdbcType=TIMESTAMP})

    SELECT count(1) FROM user_score
	WHERE user_id = #{userId,jdbcType=VARCHAR}

