mapper.select:
select user_id AS userId,nickname,avatar,1 AS status from wx_user_info where 1=1 and user_id not in(select shielduserid from user_shield where userid = #{userid}) and user_id <> #{userid}
and nickname like '%${param}%'
order by id DESC limit #{start},#{page}

select user_id AS userId,nickname,avatar,0 AS status from wx_user_info where 1=1 and user_id in (select shielduserid from user_shield where userid = #{userid} )
and nickname like '%${param}%'
order by id DESC limit #{start},#{page}
